list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>  
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>          
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>

CONSTRUCT {
    ?subject ?property ?role .
    ?role a ?objectType .
    ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?activity .
    ?activity rdfs:label ?activityLabel
} WHERE {
{
    ?subject ?property ?role .
    ?role a ?objectType .
    ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity 
} UNION
{
    ?subject ?property ?role .
    ?role a ?objectType .
    ?role <http://purl.obolibrary.org/obo/BFO_0000054>n ?activity .
    ?activity rdfs:label ?activityLabel 
}
}

PREFIX core: <http://vivoweb.org/ontology/core#>  
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>          
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>

CONSTRUCT {
    ?subject ?property ?role .
    ?role a ?objectType .
    ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .
    ?activity rdfs:label ?activityLabel 
} WHERE {
    {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity 
    } UNION
    {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity .
        ?activity rdfs:label ?activityLabel 
    }
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?role .
    ?role a ?objectType .
    ?role ?roleProperty ?roleValue . 
    ?activity rdfs:label ?activityName .
    ?activity core:grantAwardedBy ?awardedBy .
    ?awardedBy rdfs:label ?awardedByLabel .
    ?activity core:administeredBy ?adminedBy .
    ?adminedBy rdfs:label ?adminedByLabel
} WHERE {
    {
        ?subject ?property ?role 
    } UNION {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role ?roleProperty ?roleValue 
    } UNION {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . 
        ?activity rdfs:label ?activityName 
    } UNION {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://purl.obolibrary.org/obo/BFO_0000054> ?activity . 
        ?activity rdfs:label ?activityName                
    } UNION {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . 
        ?activity core:grantAwardedBy ?awardedBy
    } UNION {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . 
        ?activity core:grantAwardedBy ?awardedBy .
        ?awardedBy rdfs:label ?awardedByLabel
    } UNION {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . 
        ?activity core:administeredBy ?adminedBy
    } UNION {
        ?subject ?property ?role .
        ?role a ?objectType .
        ?role <http://vivoweb.org/ontology/core#relatedBy> ?activity . 
        ?activity core:administeredBy ?adminedBy .
        ?adminedBy rdfs:label ?adminedByLabel
    }
}
