list-view-config.query-base:

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX afn:  <http://jena.hpl.hp.com/ARQ/function#>

SELECT DISTINCT ?role (afn:localname(?role) AS ?roleName)                       
                ?indivInRole (afn:localname(?indivInRole) AS ?indivName)
                ?indivLabel
                ?roleLabel ?roleTypeLabel        
                ?dateTimeInterval ?dateTimeStart ?dateTimeEnd WHERE {

    ?subject ?property ?role 

    # We need ?subclass in query-base to get the roleTypeLabel for roles that 
    # have no label (e.g., InvestigatorRole and its subclasses)
    OPTIONAL {  ?role a ?subclass .
                ?subclass rdfs:subClassOf core:Role  
                OPTIONAL { ?subclass rdfs:label ?roleTypeLabel } 
    }

    OPTIONAL { ?roleProp rdfs:subPropertyOf core:roleOf .
               ?role ?roleProp ?indivInRole 
               OPTIONAL { ?indivInRole rdfs:label ?indivLabel } 
    }

    OPTIONAL { ?role rdfs:label ?roleLabel }

    OPTIONAL { ?role core:dateTimeInterval ?dateTimeInterval 
               OPTIONAL { ?dateTimeInterval core:start ?dateTimeStartValue .                    
                          ?dateTimeStartValue core:dateTime ?dateTimeStart 
               }
               OPTIONAL { ?dateTimeInterval core:end ?dateTimeEndValue .
                          ?dateTimeEndValue core:dateTime ?dateTimeEnd
              }
    }
    # Make sure we get the role that goes with the role property
    FILTER (
       ( ?roleProp != core:investigatorRoleOf || ?subclass = core:InvestigatorRole ) &&
       ( ?roleProp != core:principalInvestigatorRoleOf || ?subclass = core:PrincipalInvestigatorRole ) &&
       ( ?roleProp != core:co-PrincipalInvestigatorRoleOf || ?subclass = core:CoPrincipalInvestigatorRole )              
    )       
} ORDER BY ?indivName ?indivLabel ?roleLabel ?roleTypeLabel

list-view-config.query-collated:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#>
SELECT DISTINCT ?subclass ?role (afn:localname(?role) AS ?roleName) ?indivInRole (afn:localname(?indivInRole) AS ?indivName) ?indivLabel ?roleLabel ?roleTypeLabel ?dateTimeInterval ?dateTimeStart ?dateTimeEnd WHERE {
?subject ?property ?role
OPTIONAL { ?role a ?subclass . ?subclass rdfs:subClassOf core:Role OPTIONAL { ?subclass rdfs:label ?roleTypeLabel } }
OPTIONAL { ?roleProp rdfs:subPropertyOf core:roleOf . ?role ?roleProp ?indivInRole OPTIONAL { ?indivInRole rdfs:label ?indivLabel } }
OPTIONAL { ?role rdfs:label ?roleLabel }
OPTIONAL { ?role core:dateTimeInterval ?dateTimeInterval OPTIONAL { ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } OPTIONAL { ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd } } # Make sure we get the role that goes with the role property FILTER ( ( ?roleProp != core:investigatorRoleOf || ?subclass = core:InvestigatorRole ) && ( ?roleProp != core:principalInvestigatorRoleOf || ?subclass = core:PrincipalInvestigatorRole ) && ( ?roleProp != core:co-PrincipalInvestigatorRoleOf || ?subclass = core:CoPrincipalInvestigatorRole ) ) } ORDER BY ?subclass ?indivName ?indivLabel ?roleLabel ?roleTypeLabel

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>  
CONSTRUCT {
    ?subclass rdfs:subClassOf core:Role 
} WHERE {
    ?subclass rdfs:subClassOf core:Role 
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>  
CONSTRUCT {
    ?roleProp rdfs:subPropertyOf core:roleOf 
} WHERE {
    ?roleProp rdfs:subPropertyOf core:roleOf 
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?role .
    ?role ?roleProperty ?roleValue .
    ?subclass rdfs:label ?roleTypeLabel .
    ?indivInRole rdfs:label ?indivLabel
} WHERE {
    {
       ?subject ?property ?role 
    } UNION {
       ?subject ?property ?role .
       ?role ?roleProperty ?roleValue . 
    } UNION {
       ?subject ?property ?role .
       ?role a ?subclass . 
    } UNION {
       ?subject ?property ?role .
       ?role a ?subclass . 
       ?subclass rdfs:label ?roleTypeLabel
    } UNION {
       ?subject ?property ?role .
       ?role ?roleProp ?indivInRole .
       ?indivInRole rdfs:label ?indivLabel
    }
}

PREFIX core: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
} WHERE {
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:start ?dateTimeStartValue .
    ?dateTimeStartValue core:dateTime ?dateTimeStart 
}

PREFIX core: <http://vivoweb.org/ontology/core#>    
CONSTRUCT { 
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
} WHERE {
    ?subject ?property ?role .  
    ?role core:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval core:end ?dateTimeEndValue .
    ?dateTimeEndValue core:dateTime ?dateTimeEnd 
}
